import React from 'react';
import styled from 'styled-components';

const TodoStyle = styled.div `
width: 1162px;
height: 116px;
background: #FFFFFF;
box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.25);
.item{
    color: red;
    cursor: pointer;
}
.item.completed {
    color: black;
    text-decoration: line-through;
}
`

const Todo = props => {
    const completed = props.item.completed ?  ' Completed' : ''
    return (
       <TodoStyle>
        <div
        className={`item ${completed}`} key={props.item.id}
        onClick={() => props.toggleItem(props.item.id)}
        >
            <p>{props.item.task}</p>
            <button className="delete-btn" onClick={() => {props.deleteTasks(props.item.id)}}>
                    Delete
                </button>
        </div>
        </TodoStyle>
    )
}
export default Todo;
