import React, {Component} from 'react';
import styled from 'styled-components';

const TodoStyle = styled.div `
.item{
    color: red;
    cursor: pointer;
}
.item.completed {
    color: black;
    text-decoration: line-through;
}
`

class Todo extends Component {

   render(){
    return (
       <TodoStyle>
        <div
        className={`item ${props.item.completed ?  ' Completed' : ''}`} key={props.item.id}
        onClick={() => props.toggleItem(props.item.id)}
        >
            <p>{props.item.task}</p>
            <button className="delete-btn" onClick={this.props.deleteTasks(this.props.id)}>
                    Delete
                </button>
        </div>
        </TodoStyle>
    )
}
}
export default Todo;
